import React, { useState } from "react";
import {Link} from 'react-router-dom';
import "./NavBar.css";
import { Navbar, Container, Offcanvas, Nav, NavDropdown } from "react-bootstrap";
import gelatoLogo from "./Icons/gelatoLogo.svg";
import basket from "./Icons/basket.svg";
import user from "./Icons/user.svg";


function NavBarComponent() {
    const [changeAccount, setChangeAccount] = useState(false);
    const [changeBasket, setChangeBasket] = useState(false);


    const handleClickAccount = () => {
        setChangeAccount (!changeAccount);
    }

    const handleClickBasket = () => {
        setChangeBasket (!changeBasket);
    }



    return (
        <Navbar expand="md" bg="white" className="sticky-top">
            <Container fluid>
                <Navbar.Toggle aria-controls="offcanvasNavbar" />

                <Navbar.Brand as={Link} to="/home" className="order-1 order-md-2 order-lg-1">
                    <img width="120px" height="75px" className="img-responsive" src={gelatoLogo} alt="logoGoHome" />
                </Navbar.Brand>

                <Navbar.Offcanvas id="offcanvasNavbar" aria-labelledby="offcanvasNavbarLabel" lacement="start" >

                    {/* close menu button */}
                    <Offcanvas.Header closeButton>
                        <Offcanvas.Title id="offcanvasNavbarLabel" className="text-center align-item-center"> </Offcanvas.Title>
                    </Offcanvas.Header>

                    {/* mobile menu title */}
                    <p className="text-center fw-bold text-black h4">MENU</p>

                    {/* mobile menu */}
                    <Offcanvas.Body>
                        <Nav className="justify-content-end flex-grow-1 fw-bold pe-3">
                            <NavDropdown.Divider />
                            <Nav.Link as={Link} to="/home" className=" mx-1 pt-4 myFirstFont text-black"> HOME </Nav.Link>
                            <Nav.Link as={Link} to="/gelato" className="mx-1 text-black"> GELATO </Nav.Link>
                            <Nav.Link as={Link} to="/gelatoSpecial" className="mx-1 text-black"> GELATO FOR SPECIAL NEEDS </Nav.Link>
                            <Nav.Link as={Link} to="/donuts" className="mx-1 text-black"> DONUTS </Nav.Link>
                            <Nav.Link as={Link} to="/donutsSpecial" className="mx-1 text-black"> DONUTS FOR SPECIAL NEEDS </Nav.Link>
                        </Nav>

                        <NavDropdown.Divider className="d-sm-none" />

                        <Nav.Link as={Link} to="/account"  className="d-xs-inline-flex d-sm-none text-black fw-bold" >
                            <img width="16px" height="21px" className="img-responsive m-2" src={user} alt="accountIcon" />
                            Account
                        </Nav.Link>

                        <NavDropdown.Divider className="d-sm-none" />

                        <Nav.Link as={Link} to="/basket" className="d-xs-inline-flex d-sm-none text-black fw-bold" >
                            <img width="16px" height="21px" className="img-responsive m-2" src={basket} alt="basketIcon" />
                            Basket
                        </Nav.Link>

                    </Offcanvas.Body>
                </Navbar.Offcanvas>

                {/* desktop menu */}
                <Nav className="me-auto mx-3 pt-4 d-none d-md-inline-flex order-md-2 order-lg-2">
                    <Nav.Link as={Link} to="/gelato" className="nav-link mx-3 active "> <p className="hoverMenu ">Gelato</p> </Nav.Link>
                    <Nav.Link as={Link} to="/gelatoSpecial" className="nav-link mx-2"> <p className="hoverMenu">Gelato for special needs</p> </Nav.Link>
                    <Nav.Link as={Link} to="/donuts" className="nav-link mx-3 "> <p className="hoverMenu">Donuts</p> </Nav.Link>
                    <Nav.Link as={Link} to="/donutsSpecial" className="nav-link mx-2"> <p className="hoverMenu">Donuts for special needs</p> </Nav.Link>
                </Nav>

                {/* account/basket menu */}
                <Nav className="d-flex flex-row order-3 order-md-3 order-lg-3 ">
                    <Nav.Link as={Link} to="/account" className="row d-none d-sm-inline" onClick={handleClickAccount}>
                        <img width="24px" height="24px" className={changeAccount ? 'activeImageAccount img-responsive align-item-center' : 'nonActivImageAccount img-responsive align-item-center'}  alt="accountIcon" />
                        <p className= {changeAccount ? "activeTextAccount text-center hoverMenu" : "nonActivTextAccount text-center hoverMenu"}>Account</p>
                    </Nav.Link>
                    <Nav.Link as={Link} to="/basket"  className="row mx-1"onClick={handleClickBasket}>
                        <img width="242px" height="24px" className={changeBasket ? "activeImageBasket img-responsive" : "nonActivImageBasket img-responsive"}  alt="basketIcon" />
                    <p className={changeBasket ? "activeTextBasket text-center hoverMenu" : "nonActivTextBasket text-center hoverMenu"}>Basket</p>
                    </Nav.Link>
                </Nav>
            </Container>
        </Navbar>
    );
}
export default NavBarComponent;
